svg.script:
	
	/**/	
			
			var endInterval = 0
			
			var rot1_1 = 0, rot1_2 = 15, rot1_3 = 40, rot1_4 = -5;
			var rot2_1 = 0, rot2_2 = -60, rot2_3 = -30, rot2_4 = 20;	
			var rot3_1 = 0, rot3_2 = -70, rot3_3 = 30, rot3_4 = -70;	
			
			var sand_1 = 0, sand_2 = 0;
			
			var fast_ = 50, fast_2 = 50, fast_3 = 50, fast_4 = 50, fast_5 = 50, fast_6 = 50;

			var rota = 0;
			
			var endAnimation = false;			
			
			var aniPoints = 0;
			
			var main_obj = document.getElementById("main_obj");
			var leaver_1 = document.getElementById("leaver_1");
			var leaver_2 = document.getElementById("leaver_2");
			var leaver_3 = document.getElementById("leaver_3");
			
			var sand_flow = document.getElementById("maskObj");
			
			
			var obj3 = document.getElementById("obj3");
			
			var Interval = null;
			
			function startAnimation() {
				if(Interval == null) {
					Interval = setInterval(animate, 50);
				}
			}
			function stopAnimation() {
				if(Interval != null){
					clearInterval(Interval);
					Interval = null;
				}
			}	
			
			function animate() {	
				
				rota = rota + 1;
				
				aniPoints = aniPoints +1;
				
				if(aniPoints > 10 && aniPoints < 40){
					endAnimation = false;
					fast_ += (2 - fast_)/10;
					rot1_1 += (rot1_2 - rot1_1)/fast_;				
					rot2_1 += (rot2_2 - rot2_1)/fast_;
					leaver_1.setAttribute("transform",  "rotate("+ rot1_1 +" 20  156)");
					leaver_2.setAttribute("transform",  "rotate("+ rot2_1 +" 120  35)");
				}
				
				if(aniPoints > 30 && aniPoints < 50){					
					fast_2 += (2 - fast_2)/10;					
					rot3_1 += (rot3_2 - rot3_1)/fast_2;					
					leaver_3.setAttribute("transform",  "rotate("+ rot3_1 +" 165  120)");
				}
				
				if(aniPoints > 50 && aniPoints < 80){					
					fast_3 += (2 - fast_3)/10;						
					rot1_1 += (rot1_3 - rot1_1)/fast_3;					
					leaver_1.setAttribute("transform",  "rotate("+ rot1_1 +" 20  156)");
				}
				
				if(aniPoints > 80 && aniPoints < 120){						
					fast_4 += (2 - fast_4)/10;						
					rot3_1 += (rot3_3 - rot3_1)/fast_4;	
					rot2_1 += (rot2_3 - rot2_1)/fast_4;					
					leaver_3.setAttribute("transform",  "rotate("+ rot3_1 +" 165  120)");
					leaver_2.setAttribute("transform",  "rotate("+ rot2_1 +" 120  35)");
				}
				
				if(aniPoints > 120 && aniPoints < 150){					
					
					fast_5 += (2 - fast_5)/10;						
					rot1_1 += (rot1_4 - rot1_1)/fast_5;	
					rot2_1 += (rot2_4 - rot2_1)/fast_5;						
					leaver_1.setAttribute("transform",  "rotate("+ rot1_1 +" 20  156)");
					leaver_2.setAttribute("transform",  "rotate("+ rot2_1 +" 120  35)");
				}

				
				if(aniPoints > 160 && aniPoints < 190){					
					fast_6 += (2 - fast_6)/10;	
					rot3_1 += (rot3_4 - rot3_1)/fast_6;									
					leaver_3.setAttribute("transform",  "rotate("+ rot3_1 +" 165  120)");
					
				}
				
				if(aniPoints > 185 && aniPoints < 220){
					sand_1 += (100 - sand_1)/10;									
					sand_flow.setAttribute("height",  sand_1 );
				}
				
				if(aniPoints > 220 && aniPoints < 240){
					sand_2 += (40 - sand_2)/2;									
					sand_flow.setAttribute("transform",  "translate( 0, "+sand_2+")");
				}
				
				
				if(aniPoints > 260 && !endAnimation){					
					endAnimation = true;
					aniPoints = 0;
					fast_ = 50, fast_2 = 50, fast_3 = 50, fast_4 = 50, fast_5 = 50, fast_6 = 50;
					sand_1 = 0;
					sand_2 = 0;
					sand_flow.setAttribute("height",  0 );
					sand_flow.setAttribute("transform",  "translate( 0, "+0+")");
				}
				
			}			

			document.getElementById("sand").setAttribute("transform",  "translate( 215, 165)");
			
			main_obj.setAttribute("transform",  "translate( 100, 10)");			
				
			startAnimation();
